
DROP VIEW IF EXISTS `vw_entry_form_attributes`;
CREATE VIEW `vw_entry_form_attributes`  AS  select `f`.`form` AS `form`,`t`.`language_code` AS `language_code`,`f`.`display_sequence` AS `display_sequence`,`a`.`id` AS `id`,`a`.`data_type` AS `data_type`,`t`.`translation` AS `caption`,`t`.`hint` AS `hint`,`a`.`min` AS `min`,`a`.`max` AS `max`,`a`.`is_write_once` AS `is_write_once`,`a`.`default` AS `default` from ((`attributes` `a` join `form_attributes` `f` on((`f`.`attribute` = `a`.`id`))) join `attribute_translations` `t` on((`t`.`attribute_id` = `a`.`id`))) where (`f`.`hide_on_entry` = 0) order by `f`.`form`,`t`.`language_code`,`f`.`display_sequence` ;
